import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './BookList.css';
import { useAppContext } from './context/appContext';

const API = 'https://example-data.draftbit.com/books?_limit=240';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const { favorites, addToFavorites, removeFromFavorites } = useAppContext();

  console.log('favorites are', favorites);

  const favoritesChecker = (id) => {
    const boolean = favorites.some((book) => book.id === id);
    return boolean;
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Fetch books based on the search query
    axios
      .get(`${API}&q=${searchQuery}`)
      .then((res) => {
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    // Fetch all books when the component mounts
    axios
      .get(API)
      .then((res) => {
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className='book-list'>
      {/* Search Form */}
      <form onSubmit={handleSearchSubmit}>
        <input
          type='text'
          placeholder='Search books...'
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type='submit'>Search</button>
      </form>

      {books.map((book) => (
        <div key={book.id} className='book-entry'>
          <div>
            <h2>{book.title}</h2>
          </div>
          <Link key={book.id} to={`/book/${book.id}`} className='book-entry-link'>
            <div>
              <img src={book.image_url} alt='#' />
            </div>
          </Link>
          <div>
            {favoritesChecker(book.id) ? (
              <button onClick={() => removeFromFavorites(book.id)}>
                Remove from Favorites
              </button>
            ) : (
              <button onClick={() => addToFavorites(book)}>Add to Favorites</button>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default BookList;
