import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./Homepage.css";
import UserContext from "../auth/UserContext";
import SearchForm from "../SearchForm/SearchForm";

function Homepage() {
  const { currentUser } = useContext(UserContext);
  console.debug("Homepage", "currentUser=", currentUser);

  return (
    <div className="Homepage">
      <div className="container text-center">
        <h1 className="mb-4 font-weight-bold">BookHub</h1>
        <p className="lead">All the books in one, convenient place.</p>
        {currentUser ? (
          <div className='header-content flex flex-c text-center text-white'>
            <h2>
              Welcome Back, {currentUser.firstName || currentUser.username}!
            </h2>
            <h2 className='header-title text-capitalize'>Find your book of choice.</h2><br />
            <p className='header-text fs-18 fw-3'>Find any book that you desire! You can see the details of the book by just clicking the title. Happy Readings!</p>
            <SearchForm />
          </div>
        ) : (
          <p>
            <Link className="btn btn-primary font-weight-bold mr-3" to="/login">
              Log in
            </Link>
            <Link className="btn btn-primary font-weight-bold" to="/signup">
              Sign up
            </Link>
          </p>
        )}
      </div>
    </div>
  );
}

export default Homepage;
