// Homepage.js
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./Homepage.css";
import UserContext from "../auth/UserContext";

function Homepage() {
  const { currentUser } = useContext(UserContext);
  console.debug("Homepage", "currentUser=", currentUser);

  return (
    <div className="Homepage">
      <div className="container text-center">
        <h1 className="mb-4 font-weight-bold">BookHub</h1>
        <p className="lead">All the books in one, convenient place.</p>
        {currentUser ? (
          <h2>
            Welcome Back, {currentUser.firstName || currentUser.username}!
          </h2>
        ) : (
          <p>
            <Link className="btn btn-primary font-weight-bold mr-3" to="/login">
              Log in
            </Link>
            <Link className="btn btn-primary font-weight-bold" to="/signup">
              Sign up
            </Link>
          </p>
        )}
      </div>

      <div className="about-section container text-center mt-5">
        <h2 className="mb-4 font-weight-bold">About BookHub</h2>
        <p>
          BookHub is your go-to platform for discovering and managing your favorite books.
          Find, explore, and add books to your collection with ease.
        </p>
        <img
          src="/path/to/bookcases-image.jpg"
          alt="Bookcases"
          className="bookcases-image"
        />
      </div>
    </div>
  );
}

export default Homepage;
