import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Loading from '../Loader/Loader';
import coverImg from '../../images/cover_not_found.jpg';
import './BookDetails.css';
import { FaArrowLeft } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const BookDetails = () => {
  const { id } = useParams();
  const [loading, setLoading] = useState(false);
  const [book, setBook] = useState(null);
  const navigate = useNavigate();



  useEffect(() => {
    setLoading(true);

    const fetchBookDetails = async () => {
      try {
        const response = await fetch(`https://www.googleapis.com/books/v1/volumes/${id}`);
        const data = await response.json();

        if (data.volumeInfo) {
          const {
            description,
            title,
            imageLinks,
            categories,
            publishedDate,
            authors,
          } = data.volumeInfo;

          const newBook = {
            description: description || 'No description found',
            title,
            cover_img: imageLinks ? imageLinks.thumbnail : coverImg,
            subject_places: categories ? categories.join(', ') : 'No subject places found',
            subject_times: 'N/A',
            subjects: 'N/A',
            published_date: publishedDate || 'N/A',
            authors: authors ? authors.join(', ') : 'No authors found',
          };

          setBook(newBook);
        } else {
          setBook(null);
        }

        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(false);
      }
    };

    fetchBookDetails();
  }, [id]);

  if (loading) return <Loading />;

  return (
    <section className='book-details'>
      <div className='container'>
        <button type='button' className='flex flex-c back-btn' onClick={() => navigate('/book')}>
          <FaArrowLeft size={22} />
          <span className='fs-18 fw-6'>Go Back</span>
        </button>

        <div className='book-details-content grid'>
          <div className='book-details-img'>
            <img src={book?.cover_img} alt='cover img' />
          </div>
          <div className='book-details-info'>
            <div className='book-details-item title'>
              <span className='fw-6 fs-24'>{book?.title}</span>
            </div>
            <div className='book-details-item description'>
              <span>{book?.description}</span>
            </div>
            <div className='book-details-item'>
              <span className='fw-6'>Authors: </span>
              <span className='text-italic'>{book?.authors}</span>
            </div>
            <div className='book-details-item'>
              <span className='fw-6'>Published Date: </span>
              <span className='text-italic'>{book?.published_date}</span>
            </div>
            <div className='book-details-item'>
              <span className='fw-6'>Subject Places: </span>
              <span className='text-italic'>{book?.subject_places}</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BookDetails;
